(366, 1, '2024-07-30 00:30:17', '2024-07-30 00:30:17', '<!-- wp:group {\"layout\":{\"type\":\"constrained\"}} -->\n<div class=\"wp-block-group\"><!-- wp:image {\"id\":77,\"sizeSlug\":\"full\",\"linkDestination\":\"none\"} -->\n<figure class=\"wp-block-image size-full\"><img src=\"http://armoniamatutina.test/wp-content/uploads/2024/07/hero-scaled.jpg\" alt=\"\" class=\"wp-image-77\"/></figure>\n<!-- /wp:image -->\n\n<!-- wp:quote -->\n<blockquote class=\"wp-block-quote\"><!-- wp:quote -->\n<blockquote class=\"wp-block-quote\"><!-- wp:paragraph {\"align\":\"left\"} -->\n<p class=\"has-text-align-left\"><strong>Descripcion de imagen</strong> <a href=\"https://www.armoniamatutina.com/\" data-type=\"link\" data-id=\"https://es.wikipedia.org/wiki/Murasaki_Shikibu\">(Autor)</a></p>\n<!-- /wp:paragraph --></blockquote>\n<!-- /wp:quote --></blockquote>\n<!-- /wp:quote --></div>\n<!-- /wp:group -->', 'Imagen con copyright', '', 'inherit', 'closed', 'closed', '', '364-revision-v1', '', '', '2024-07-30 00:30:17', '2024-07-30 00:30:17', '', 364, 'http://armoniamatutina.test/?p=366', 0, 'revision', '', 0);
